/**
 * @Author:       Chen
 * @Date:         2021/3/1 14:29
 * @Description:  395. 至少有 K 个重复字符的最长子串
 *
 * 给你一个字符串 s 和一个整数 k ，请你找出 s 中的最长子串，
 * 要求该子串中的每一字符出现次数都不少于 k 。返回这一子串的长度。
 * 示例 1：
 *
 * 输入：s = "aaabb", k = 3
 * 输出：3
 * 解释：最长子串为 "aaa" ，其中 'a' 重复了 3 次。
 * 示例 2：
 *
 * 输入：s = "ababbc", k = 2
 * 输出：5
 * 解释：最长子串为 "ababb" ，其中 'a' 重复了 2 次， 'b' 重复了 3 次。
 *
 * 提示：
 * 1 <= s.length <= 104
 * s 仅由小写英文字母组成
 * 1 <= k <= 105
 *
 * 来源：力扣（LeetCode）
 * 链接：https://leetcode-cn.com/problems/longest-substring-with-at-least-k-repeating-characters
 * 著作权归领扣网络所有。商业转载请联系官方授权，非商业转载请注明出处。
 */
fun main() {
	val l = System.currentTimeMillis()
	println(
//		longestSubstring(
//			"",
//			301
//		)
	longestSubstring("aaabb",3)
	)
	val s = SimpleDateFormat("mm分ss秒SSS毫秒").format(System.currentTimeMillis() - l)
	println("共耗时:$s")
}

fun longestSubstring(s: String, k: Int): Int {
	return slidingWindow(s,  k)
}

fun slidingWindow(s: String, k: Int): Int {
	var ans = 0
	val n = s.length
	for (t in 1..26) {
		var l = 0
		var r = 0
		val cnt = IntArray(26)
		var total = 0
		var less = 0
		while (r < n) {
			cnt[s[r] - 'a']++
			if (cnt[s[r] - 'a'] == 1) {
				total++
				less++
			}
			if (cnt[s[r] - 'a'] == k) {
				less--
			}
			while (total > t) {
				cnt[s[l] - 'a']--
				if (cnt[s[l] - 'a'] == k - 1) {
					less++
				}
				if (cnt[s[l] - 'a'] == 0) {
					less--
					total--
				}
				l++
			}
			if (less == 0) {
				ans = max(ans, r - l + 1)
			}
			r++
		}
	}
	return ans
}

fun dfs(s: String, l: Int, r: Int, k: Int): Int {

	val cnt = IntArray(26)
	s.forEach { ch ->
		cnt[ch - 'a']++
	}
	var split: Char = 0.toChar()
	for (it in 0..25) {
		if (cnt[it] in 1 until k) {
			split = (it + 'a'.toInt()).toChar()
			break
		}
	}
	if (split == 0.toChar()) {
		return r - l + 1
	}
	var i = l
	var length = 0
	var ans = 0
	while (i <= r) {
		while (i <= r && s[i] == split) {
			i++
		}
		if (i > r) {
			break
		}
		val start = i
		while (i <= r && s[i] != split) {
			i++
		}

		length = dfs(s, start, i - 1, k)
		ans = max(ans, length)
	}
	return ans
}